import React from 'react';
import './App.css';

class App extends React.Component {
  state = {
    age: 21
  }

  increseAge = () => {
    this.setState({
      ...this.state,
      age: ++this.state.age
    })
  }
  render() {
    return (
      <div className="App" >
        <div>Age: <span>{this.state.age}</span></div>
        <button onClick={this.on}>Increse Age</button>
        <button>Decrese Age</button>
      </div>
    );
  };
}

export default App;
